mapper.update:
update upload_field a
set a.field_name = #{fieldName},
    a.field_desc = #{fieldDesc},
    a.field_type = #{fieldType},
    a.field_order = #{fieldOrder}
where a.field_id = #{fieldId}

update upload_field a
set a.field_code = #{fieldCode}
where a.field_id = #{fieldId}

mapper.delete:
delete from upload_option where field_id in (
    select field_id from upload_field where form_id = #{formId} and field_type in ('radio','checkbox')
)
